DROP TABLE IF EXISTS `blogroll`;
CREATE TABLE `blogroll` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '友链id',
  `url` varchar(255) NOT NULL COMMENT '友链URL',
  `name` varchar(255) NOT NULL COMMENT '友链名称',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=10 DEFAULT CHARSET=utf8 COMMENT='友情链接表';

