INSERT INTO `certificate` VALUES ('15', '红十字会急救员认证', null, null, null, '2016-12-12', '无', '1000', '<img src=\"http://api.map.baidu.com/staticimage?center=121.617433%2C31.430763&zoom=12&width=558&height=360&markers=121.617433%2C31.430763&markerStyles=l%2CA\" alt=\"\" />', 'http://houhou.com', 'http://202.206.100.34:8080/andacms/export/www/resources/40/2015122111165657.jpg');

-- ----------------------------
-- Table structure for forum
-- ----------------------------
DROP TABLE IF EXISTS `forum`;
CREATE TABLE `forum` (
  `forumid` int(11) NOT NULL AUTO_INCREMENT,
  `typeid` int(2) NOT NULL DEFAULT '4' COMMENT '论坛分类',
  `userid` int(11) NOT NULL,
  `title` varchar(100) NOT NULL COMMENT '论坛标题',
  `content` text NOT NULL COMMENT '论坛内容',
  `posttime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '提交时间',
  `readcount` int(11) NOT NULL DEFAULT '0' COMMENT '阅读量',
  PRIMARY KEY (`forumid`)
) ENGINE=MyISAM AUTO_INCREMENT=65 DEFAULT CHARSET=utf8 COMMENT='论坛表';

-- ----------------------------
-- Records of forum
-- ----------------------------
INSERT INTO `forum` VALUES ('34', '1', '5', '计算机四级证书好考吗？', '<p><img src=\"http://img.baidu.com/hi/jx2/j_0013.gif\"/><img src=\"http://img.baidu.com/hi/jx2/j_0013.gif\"/><img src=\"http://img.baidu.com/hi/jx2/j_0013.gif\"/></p>', '2016-12-06 11:09:24', '20');
INSERT INTO `forum` VALUES ('33', '1', '82', '大学可考证有什么？', '<p>大学可考证有什么？从什么时候开始比较好？考证重要吗？专业是电子商务类，大二会再分信息管理，物流，电子商务。想读信息管理。</p>', '2016-12-04 16:02:02', '5');
INSERT INTO `forum` VALUES ('30', '1', '82', '大学考证到底是怎么一回事？', '<p>大一就考掉吗？证书有什么期限吗？在学校报考好还是网上报考好？<img src=\"http://img.baidu.com/hi/jx2/j_0046.gif\"/></p>', '2016-12-01 16:12:09', '0');
INSERT INTO `forum` VALUES ('6', '2', '2', '计算机专业大一时期应该参加什么比赛？', '  本人大一刚刚进入大学，请问学长们我除了学习专业课程之外，应如何参加什么比赛充实自己的大学生活呢？', '2016-11-29 11:05:17', '1');
INSERT INTO `forum` VALUES ('58', '4', '51', '纠结考研要不要跨考', '<p>我不喜欢现在的专业，但是我还是要考研，我很纠结，不知道要怎么办，也不知道应该做什么，也没有具体感兴趣的专业<img src=\"http://img.baidu.com/hi/jx2/j_0066.gif\"/></p>', '2016-12-19 16:45:09', '4');
