CREATE TABLE IF NOT EXISTS `certificate` (
  `cid` int(11) NOT NULL AUTO_INCREMENT,
  `cname` varchar(255) NOT NULL COMMENT '证件名称',
  `keyword` varchar(40) DEFAULT NULL COMMENT '关键词',
  `keyword2` varchar(40) DEFAULT NULL COMMENT '关键词',
  `keyword3` varchar(40) DEFAULT NULL COMMENT '关键词',
  `ctime` date NOT NULL COMMENT '考证时间',
  `crequest` varchar(100) NOT NULL COMMENT '考证要求',
  `cfee` int(11) NOT NULL COMMENT '考证费用',
  `ccontent` text NOT NULL COMMENT '详情内容',
  `curl` varchar(255) NOT NULL COMMENT '官网',
  `cthumb` varchar(255) NOT NULL DEFAULT 'http://www.hebtu.edu.cn/template/0de2d533795b48688b0776bfade53f5a/20140828143712866/images/banner_03.jpg' COMMENT '缩略图地址',
  PRIMARY KEY (`cid`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=16 ;

