--
-- 表的结构 `forum`
--

CREATE TABLE IF NOT EXISTS `forum` (
  `forumid` int(11) NOT NULL AUTO_INCREMENT,
  `typeid` int(2) NOT NULL DEFAULT '4' COMMENT '论坛分类',
  `userid` int(11) NOT NULL,
  `title` varchar(100) NOT NULL COMMENT '论坛标题',
  `content` text NOT NULL COMMENT '论坛内容',
  `posttime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '提交时间',
  `readcount` int(11) NOT NULL DEFAULT '0' COMMENT '阅读量',
  PRIMARY KEY (`forumid`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COMMENT='论坛表' AUTO_INCREMENT=106 ;

--
-- 转存表中的数据 `forum`
--

INSERT INTO `forum` (`forumid`, `typeid`, `userid`, `title`, `content`, `posttime`, `readcount`) VALUES
(34, 1, 5, '计算机四级证书好考吗？', '<p><img src="http://img.baidu.com/hi/jx2/j_0013.gif"/><img src="http://img.baidu.com/hi/jx2/j_0013.gif"/><img src="http://img.baidu.com/hi/jx2/j_0013.gif"/></p>', '2016-12-06 03:09:24', 28),
(85, 2, 96, '将来想从事手机或者软件开发的工作是否需要参加ACM竞赛？', '<p>我是大一学生，在一所排名比较靠后的985，学的是软件工程，然后学校有一个ACM竞赛的组，开学考试筛选出了大概30左右个人，我被选上了，然后开始培训，老师说这个学期结束后留校培训10天再回家，寒假回来机试留下10个或者10多个人。<br/>但是我的兴趣是做手机开发，编写应用之类的，一直都对这个蛮感兴趣，我知道刷ACM题是非常必要的，但是如果加入ACM组的话就意味着以后很多时间就是刷竞赛题，但是我对这个，实在……但是我又觉得就这样放弃好像又有点可惜。<p>问下ACM 算法什么的对软件开发来说很重要吗？我也不太明白，感觉ACM题都是数学题，软件开发需要用到吗？感觉关系好像不大啊。好吧我不太懂，乱说的，希望能有大神解答一下吧。</p></p>', '2016-12-25 08:03:53', 23),
(84, 2, 96, '我今年大一，学院9月有个软件设计比赛，我应该怎么弄呢？', '<p>我今年大一，也是大学才刚接触编程，学院9月有个软件设计比赛，我报名了，我准备弄安卓软件开发，软件也想好了，我现在要怎么下手呢？还有是不是先要软件框架设计?这些都怎么弄额。。刚开始真的不知道怎么弄？另外小组5个人基础都差不多，大家有都是抱着试试看的心态。<br/>java大家其实都不会，不过还有整个暑假的时间，应该可以吧</p>', '2016-12-25 08:03:16', 3),
(83, 2, 96, '大三了，学软件的，参加不少比赛，现在浮躁的有些不想上学了。', '<p>大三了，学计算机软件的，参加不少比赛，现在浮躁的有些不想上学了。我该怎么办？</p>', '2016-12-25 08:02:27', 18),
(82, 4, 96, '非计算机软件专业，可以通过准备哪项考试或竞赛间接培养自己的编程能力？', '<p>比如国家计算机等级考试，软件工程师，程序员之类的。我知道这些证本身都没什么意义，我就是想通过准备考试，有一个大致的方向，接触更多计算机编程的思想。</p>', '2016-12-25 07:44:48', 0),
(80, 2, 96, '蓝桥软件比赛获奖证书有用吗？', '<p>就是蓝桥杯软件比赛，有预赛和决赛，听说得奖了有公司的直接面试资格<img src="http://img.baidu.com/hi/jx2/j_0016.gif"/></p>', '2016-12-25 07:43:01', 0),
(81, 2, 96, '大家对于"全国软件专业人才设计与创业大赛"这样的比赛有什么看法?', '<p>我在学校的软件实验室学了两年，做过一些软件开发。对java的掌握还可以，有老师希望我参加这个比赛，但是带我的老师说这种比赛没什么含金量，不如踏踏实实在实验室学习。想问问大家的看法。<img src="http://img.baidu.com/hi/jx2/j_0023.gif"/></p>', '2016-12-25 07:43:38', 5),
(33, 1, 82, '大学可考证有什么？', '<p>大学可考证有什么？从什么时候开始比较好？考证重要吗？专业是电子商务类，大二会再分信息管理，物流，电子商务。想读信息管理。</p>', '2016-12-04 08:02:02', 7),
(12, 1, 2, '三本大学里好多人不上课，不考证，光想着以后凭关系就能立足，大多数人甚至连关系都没有。身处这样的环境，该如何勉励自己不断前进？', '<p>每天都在讨论日本动漫，lol和烟酒棋牌的言论中生存，想找到能发泄压力，有共同语言的人都很难，这些人每天这么过，是什么支撑他们如此心安理得？</p>', '2016-11-29 03:11:11', 1),
(30, 1, 82, '大学考证到底是怎么一回事？', '<p>大一就考掉吗？证书有什么期限吗？在学校报考好还是网上报考好？<img src="http://img.baidu.com/hi/jx2/j_0046.gif"/></p>', '2016-12-01 08:12:09', 4),
(6, 2, 2, '计算机专业大一时期应该参加什么比赛？', '  本人大一刚刚进入大学，请问学长们我除了学习专业课程之外，应如何参加什么比赛充实自己的大学生活呢？', '2016-11-29 03:05:17', 6),
(58, 4, 51, '纠结考研要不要跨考', '<p>我不喜欢现在的专业，但是我还是要考研，我很纠结，不知道要怎么办，也不知道应该做什么，也没有具体感兴趣的专业<img src="http://img.baidu.com/hi/jx2/j_0066.gif"/></p>', '2016-12-19 08:45:09', 8),
(45, 3, 5, '你们大学生活费大概是多少？', '<p>刚入学小萌新求指导</p>', '2016-12-06 16:00:00', 6),
(48, 1, 5, '软件工程专业需要考什么证件？', '<p><img src="http://img.baidu.com/hi/jx2/j_0017.gif"/></p>', '2016-12-08 03:10:07', 182),
(50, 4, 5, '大学应该怎样度过呢？', '<p>抽时间找个对象，没有谈恋爱的大学是不完整的。<img src="http://img.baidu.com/hi/jx2/j_0028.gif"/></p>', '2016-12-11 16:53:58', 11),
(57, 5, 1, '跟帖评论自律管理承诺书', '<p>&nbsp; 为深入贯彻十八届四中全会依法治国的精神，落实国家互联网信息办公室“依法管网、依法办网、依法上网”的要求，本网站为完善跟帖评论自律管理，现公开作出如下承诺：<br/> &nbsp; &nbsp;一、本网站所称跟帖评论服务，是指通过运营网络互动传播技术平台，供用户对本网站传播的各类信息发表评论意见（包括但不限于语音、文字、图片、音频、视频等信息）的服务。<br/> &nbsp; &nbsp;二、本网站致力使跟帖评论成为文明、理性、友善、高质量的意见交流。在推动跟帖评论业务发展的同时，不断加强相应的信息安全管理能力，完善跟帖评论自律，切实履行社会责任，遵守国家法律法规，尊重公民合法权益，尊重社会公序良俗。<br/> &nbsp; &nbsp;三、本网站坚持用户以真实身份信息注册账号、使用跟帖评论服务。<br/> &nbsp; &nbsp;四、本网站承诺、并诚请所有用户，使用跟帖评论服务将自觉遵守不得逾越法律法规、社会主义制度、国家利益、公民合法权益、社会公共秩序、道德风尚和信息真实性等“七条底线”。<br/> &nbsp; &nbsp;五、本网站承诺、并诚请所有用户不发表下列信息：<br/> &nbsp; &nbsp;（一）反对宪法确定的基本原则的；<br/> &nbsp; &nbsp;（二）危害国家安全，泄露国家秘密，颠覆国家政权，破坏国家统一的；<br/> &nbsp; &nbsp;（三）损害国家荣誉和利益的；<br/> &nbsp; &nbsp;（四）煽动民族仇恨、民族歧视，破坏民族团结的；<br/> &nbsp; &nbsp;（五）煽动地域歧视、地域仇恨的；<br/> &nbsp; &nbsp;（六）破坏国家宗教政策，宣扬邪教和迷信的；<br/> &nbsp; &nbsp;（七）散布谣言，扰乱社会秩序、破坏社会稳定的；<br/> &nbsp; &nbsp;（八）散布淫秽、色情、赌博、暴力、凶杀、恐怖或者教唆犯罪的；<br/> &nbsp; &nbsp;（九）侮辱或者诽谤他人，侵害他人合法权益的；<br/> &nbsp; &nbsp;（十）对他人进行暴力恐吓、威胁，实施人肉搜索的；<br/> &nbsp; &nbsp;（十一）未获得未满18周岁未成年人法定监护人的书面同意，传播该未成年人的隐私信息的；<br/> &nbsp; &nbsp;（十二）散布污言秽语，损害社会公序良俗的；<br/> &nbsp; &nbsp;（十三）侵犯他人知识产权的；<br/> &nbsp; &nbsp;（十四）散布商业广告，或类似的商业招揽信息；<br/> &nbsp; &nbsp;（十五）使用本网站常用语言文字以外的其他语言文字评论的；<br/> &nbsp; &nbsp;（十六）与所评论的信息毫无关系的；<br/> &nbsp; &nbsp;（十七）所发表的信息毫无意义的，或刻意使用字符组合以逃避技术审核的；<br/> &nbsp; &nbsp;（十八）法律、法规和规章禁止传播的其他信息。<br/> &nbsp; &nbsp;六、对违反上述承诺的用户，本网站将视情况采取预先警示、拒绝发布、删除跟帖、短期禁止发言直至永久关闭账号等管理措施。对涉嫌违法犯罪的跟帖评论将保存在案、并在接受有关政府部门调查时如实报告。<br/> &nbsp; &nbsp;七、本网站承诺，完善公众举报平台，欢迎用户监督举报各类不法传播活动和违法有害信息，以共同营造清朗的网络空间。<br/></p><p><br/></p>', '2016-12-18 09:05:03', 32),
(56, 4, 2, '大学学习新闻专业靠谱吗？', '<p>题主是2017年高考文科生，希望能够从事新闻行业的工作（编辑，非记者），<br/>一是觉得中国目前还有很多人没有接受到正确的价值观指引，新闻业鱼龙混杂，希望自己是一股清流吧（2333觉得自己好中二，大概有新闻救国的这么一个想法）；<br/>二是觉得新闻涉及内容比较多，比较有意思，当然自己是喜欢政治、历史、军事之类的内容。<br/>不知道学新闻专业到底靠不靠谱？看很多学长都推荐学一个其它专业，考研再考新闻学。<br/><br/>另外本人成绩铁定是考不上清华人大复旦的…<br/>如果拼一拼，中传、武大是有希望的。<br/><br/>谢谢前辈和学长指点！<a class="zu-edit-button"><em class="zu-edit-button-icon"></em></a></p>', '2016-12-17 02:41:31', 30),
(7, 1, 55, '大学考证到底是要侧重于就业前景，还是兴趣？', '<p>本人大一，专业审计，会计从业资格证一定要考的，还是四六级什么的，我个人想考个营养师，属于个人兴趣（想更侧重自身和家人的健康），想学习这方面的知识，并没有多考虑就业前景。大学这四年到底是应该多考与自己专业相关的证书，还是考自己感兴趣的证书？</p>', '2016-11-29 03:06:11', 3),
(8, 1, 85, '你是怎么看待大学生考证的？', '<p>大家是怎么看待这件事情的？<img src="http://img.baidu.com/hi/jx2/j_0044.gif"/></p>', '2016-11-29 03:06:15', 0),
(9, 1, 2, '关于大学考证的问题，经济师、审计师、证券分析师、统计师这类证书是否重要？', '<p>我是一位会计专业的学生，目前就读于一所独立学院。想问一下，对于会计专业的学生来说，除了基本的会计师资格证、初级会计师以及CPA证书以外，是否有必要考一些比如经济师、审计师、证券分析师、统计师这样的证书？这些证书对于之后应聘其他岗位是否有帮助？</p>', '2016-11-29 03:07:00', 0),
(10, 1, 2, '大学里考证真的很重要吗？', '<p>面试的时候面试官很重视你的证书吗？</p>', '2016-11-29 03:10:10', 0),
(11, 1, 2, '假期大学同学们很多要么看书考证要么找单位实习，但自己假期只想着玩怎么办？', '<p>为此感到焦虑，怕落后同侪更多，未来就业不利。但自己就是不想假期也把自己搞得很忙怎么办，因为上学期间学习任务很重感觉很累，所以假期只想放松。</p>', '2016-11-29 03:10:25', 0),
(65, 4, 93, '好想考个证件', '<p><img src="http://img.baidu.com/hi/jx2/j_0015.gif"/></p>', '2016-12-22 11:44:34', 6),
(13, 1, 2, '大学生考证热是其实用性促使还是背后利益链推动？', '<p>如今大学生疯狂考证，其中还包括许多所谓包过班、协议班之类的。这些技能证书对于日后参加工作实用性到底多大？抑或是背后利益链推动致使？</p>', '2016-11-30 00:00:55', 0),
(14, 1, 2, '大学期间，多考证，就更容易找到工作吗？', '<p>非211、985的学生，整个班级乃至学校，最热衷的事就是考各种证。四六级不用说，甚至会计证，人力资源啦，平面设计啦，计算机二级啦，还有车证等等。然而真正令我疑惑的事，这些证中，很大一部分只要交钱（一百到两百）就轻而易举就考出来了，有些是报个班，三百块左右，也就考出来了，这么水的证真的有用吗？ 还有，很多一些老师讲课就更水了，教我们pro/e的老师，期末就出了五道题，题主就会做两个，结果79分过了。这么水的学校和老师，似乎不考证又没法证明自己会了。但自己又知道这些证说实话又太水了，我该怎么规划我的大学生活呢。</p>', '2016-11-30 00:05:33', 0),
(60, 1, 2, '在大学考证真的那么重要吗？', '<p>计算机二级过了考三级，英语四级过了考六级，普通话、驾照，还有自学考会计……这是我一个室友的生活状态。而我另外的室友虽然不至于考证这么疯狂，但是也一直在准备考六级。我问他们，为什么要考？他们回答说，反正大家都考，那就考咯，找工作肯定有用啊。而我，可能在大学算一个奇葩，英语过了四级就不打算再考其他证了，相反，我喜欢在网上听很多慕课，内容包括我很喜欢的心理学，国学之类的，跟我现在的专业完全没有关系，他们特别不屑，认为学这些对找工作完全没有用。但是说实话，英语四级他们好多都只是刚刚擦边而已，连口语资格都没得，而我的分数却比他们高得多。我就纳闷了，考这么多证难道真的能够显示你的水平吗？大学就是为了考证吗？找工作面试时，面试官只会肤浅地看你有多少证考了多少分吗？我觉得大学是应该学习，但绝不是为了敷衍而学习，也绝不是为了追求有多少证，大学应该做自己喜欢的事，有意义的事，比如我现在资助了一个老人和一个小孩，下学期主要是做志愿者，这样的事情难道不比他们疯狂地考证有意义也更锻炼人多了吗？虽然我还比较淡定，但是听到他们说起考的各种证是找工作的敲门砖，我还是有点担心，我到底要不要随大流？<a class="zu-edit-button"><em class="zu-edit-button-icon"></em></a></p>', '2016-12-22 08:56:46', 138),
(88, 3, 96, '大学生活怎么实现自己的价值？', '<p>大学。我不知道该怎么实现自己，每天日子就这么过，好像这个点就该做这个事。想发展爱好，不知干嘛，没有那么多的资金，想旅游也不能。日子一天一天过，我也想做一翻大事，实现自己的梦想。无措。似乎努力的时候也不见成果。似乎没去上课也能更快活，似乎学习不是最重要的，但是我想学好，不辜负爸妈的钱 泡进图书馆 但是最稳定的还是变化。你身边的人和自己一样，我们都蒙在鼓里，我很烦恼，这不是我想要的生活，甚至有点堕落。</p>', '2016-12-25 08:26:51', 0),
(86, 3, 96, '大学生活到底是怎样的?', '<p>真的是像很多人说的那么好么……高三的很好奇未来的大学生活，求大神们解答</p>', '2016-12-25 08:05:38', 0),
(87, 3, 96, '你大学生活最美好的时刻是？', '<p>大家畅所欲言 ，谈谈自己考大学时的奋斗时光和上了大学后精彩的人生吧！</p>', '2016-12-25 08:06:25', 0),
(90, 3, 96, '怎样才能有一个充实的大学生活？', '<p>我今年大一，因为高考还是没有成功逆袭~至少没有达到期望所以进入了一个普通二本学校。因为是压线，所以专业比较差（生物工程）。来到这里心里还是有些芥蒂，总觉得自己是比别人优秀的，但是实际只是个普通学生，身边的人也都没有什么追求。昨天思修老师说，应该向毕业三年以上的学长请教怎样过好大学生活，想听听大家的建议。</p>', '2016-12-25 08:28:57', 0),
(91, 4, 96, '大学生活中，应该把考证放在一个怎样的位置？', '<p>我是读会计的，大二一上来有很多证要考，包括英语四六级，计算机二级的几张证，还有与自己专业有关的会计从业资格证、初级会计师证等等。此外家长还希望我考教师资格证和驾照。<br/>一是我感觉现在各种考证铺天盖地而来，很大程度上挤压了我对我有兴趣的东西的追求（本身我对我的专业没什么兴趣）；二是我不是属于学霸类型，很多证的考试内容都需要我自学，需要我不断地付出更多的时间去努力准备。<br/>“鱼与熊掌不可兼得”这个道理我知道，但却不知如何摆正考证在我整个大学生涯中的位置（考证使我越来越少参加各种活动和去与人交际）。还有我不知道像现在这样广撒网地考证真的有意义吗，特别是关于自己专业的证，我感觉我以后不会从事会计方面的工作。</p>', '2016-12-25 08:29:37', 2),
(92, 3, 96, '你的大学生活，除了上课，其他时间都干了什么？', '<p>想知道大学生除了上课，其他时间都用来干了什么。</p>', '2016-12-25 08:30:29', 17),
(93, 4, 96, '在大学里不参加学生会社团班委会影响到将来的发展吗？', '<p>非211一本 主要想多读点书 学点东西 另外认为班委学生会有些黑暗 没什么活动特别锻炼人吸引自己 所以不想参加。。</p>', '2016-12-25 08:33:10', 0),
(94, 4, 96, '一个大学生不打 lol 不打篮球真的会影响到未来人际关系吗？', '<p>如题。我是个普通二本院校的本科生。身边男生基本都酷爱篮球和lol。而我偏偏一点兴趣都没有。是真的学都不想学。篮球是从小都不会。最近被家里表哥什么的提醒这样会影响到社会关系。在以后步入社会的时候没办法融入男生圈子。但我会网球。爱文玩。会自己做各种木头小物件。会电脑修理。爱篆刻，爱游泳，爱写作。我这样偏重于独自活动的人真的会成为人际交往中的弱者么。这个问题已经困扰我很久很久了。求解</p>', '2016-12-25 08:33:40', 0),
(95, 4, 96, '大学生创业会不会成为一种风潮？', '<p>《非你莫属》热播背后是大学生就业越来越难了，经济景气指数也在不断下跌，令大学生找工作更难了。与其关心大学生就业，不如关心大学生创业问题。如果未来政府部门对大学生创业给予税收优惠和机构辅导，资金支持，大学生创业会不会成为一种风潮？</p>', '2016-12-25 08:34:16', 23);

