--
-- 表的结构 `forum_comment`
--

CREATE TABLE IF NOT EXISTS `forum_comment` (
  `forum_commentid` int(11) NOT NULL AUTO_INCREMENT COMMENT '评论id',
  `forumid` int(11) NOT NULL,
  `userid` int(11) NOT NULL,
  `forum_comment` text NOT NULL COMMENT '评论内容',
  `createtime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '评论提交时间',
  `forum_answerid` int(11) NOT NULL COMMENT '回复评论id',
  PRIMARY KEY (`forum_commentid`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COMMENT='论坛评论表' AUTO_INCREMENT=52 ;

--
-- 转存表中的数据 `forum_comment`
--

INSERT INTO `forum_comment` (`forum_commentid`, `forumid`, `userid`, `forum_comment`, `createtime`, `forum_answerid`) VALUES
(9, 48, 12, '为了表示专业性，先考一个程序员（初级）。这个是国家认证的，然后有信心了就可以往上考。（可惜我大三考中级设计师的时候选择题差了一分。据说越级打怪是有门槛的，待求证。）<br/>重要性：一般能耐着性子把高级架构师考出来的，可以考虑向"烈士"方向发展，或者也去为人民服务。<br/>', '2016-12-02 03:16:00', 0),
(19, 60, 82, '<p>闲着没事考考也没啥，万一有用呢。<br/>不想考就不考，我大学一个证也没考，拿着毕业证学位证就走了，谁让咱骄傲呢，咱不靠考证吃饭。<br/>考证其实就是恐惧加随大流外加考试上瘾。<br/>当然它的用处是有的，学习没有方向依赖考证总是能学点东西的。社会如此势力，没个证证明你的能力，他们可不是伯乐，你也不是很牛逼让别人一眼就看上你。<br/>没有证的我依然骄傲，咱就想空手闯世界。让自己的路走的更艰难。咱不牛逼但有颗牛逼的心。</p>', '2016-12-20 07:13:44', 0),
(15, 34, 51, '<p>不清楚，表示没有考过</p>', '2016-12-19 08:49:32', 0),
(12, 48, 2, '<p>github 上有多个超过100 star 的项目是最好的证书。<img src="http://img.baidu.com/hi/jx2/j_0061.gif"/></p>', '2016-12-18 13:30:56', 0),
(18, 60, 32, '<p>如果你有其他方法向HR证明你比别人更优秀，那么你完全不用考证。<br/>我们都有很多自己想专心去做的事情，但我们却为了超过别人而无奈的将这些事情的实现安排在了遥远的未来。<br/>还有不要用自己有其他更高的追求来掩饰自己的慵懒，那些把证都考出来又考上研究生的人现在可是一抓一大把。<br/>如果你关系过硬，当我没说。</p><p><br/></p>', '2016-12-19 09:30:51', 0),
(21, 60, 2, '<p>在大学的考证很重要。<br/>原因有二：<br/>1.因为现在中国还是一个重视学历及资格的社会。如果你没有证书，首先就没有站在起跑线的资格，更谈不上显示能力及实力。<br/>2.大学里考资格证书相对来说比较容易，走入社会后一边工作一边学历再考取资格证书会比较难。<br/>希望对你有所帮助', '2016-12-20 07:17:11', 0),
(23, 60, 5, '<p>看到楼主的帖子，我想起了龙应台的一句话：孩子我要求你读书认真，不是要你和别人比成绩，而是因为，我希望你将来有选择的权利，选择有意义、有时间的工作，而不是被迫谋生。当你的工作在你心里有意义，你就会有成就感；当你的工作给你时间，不剥夺你的自由，你就有尊严。成就感和尊严，给你快乐。<br/>所以我想说的是，考证可能并不是证明你自己能力的一种方式，更多的是将来面对社会时你会有更多的选择权，不会因为一些硬性条件让你被剥夺做你喜欢的事情的权利。<br/>其次，大学考证的确是个不错的选择（但要在不影响你学业的情况下哦），因为真的等到你进入社会有了工作或者有了家庭，你真的很难静下心来有大学一样的心境去看书和准备考试，俗话说书到用时方恨少，多一项技能，将来就少说一句求人的话。<br/>但是，考证千万不能盲目，要提前规划好，适当的考取和专业相关证书是个不错的选择，毕竟证书也可以反映你的学习态度嘛，当然兴趣考证也可以啦。考证的过程也是一个付出的过程，好好看书认真备考，不用过多关注其他人的看法。</p>', '2016-12-20 07:38:30', 0),
(25, 33, 82, '<p>大学可考的证书非常多的、不论和你专业相不相关的都可以考。毕竟毕业以后干哪行真的是未知数。</p>', '2016-12-20 08:04:18', 0),
(26, 33, 85, '<p>除了四六级，驾照，再考个本行业里考个牛逼点的证，那些交钱给答案的就放弃好了，别浪费钱财，你一个牛逼的证比那些花里胡哨的证强多了～</p>', '2016-12-20 08:04:37', 0),
(34, 58, 2, '<p>不喜欢就考下呗<br/>至少待学校&nbsp;还能稍微缓下几年<img src="http://img.baidu.com/hi/tsj/t_0021.gif"/></p>', '2016-12-20 14:59:48', 0),
(40, 95, 96, '<p>穷人家的孩子大部分首先考虑考公务员。（被欺压过的人，总想看看有没有出头之日）<br/>中等人家的孩子大部分想进国企。（稳定压倒一切）<br/>富人家的孩子都想去创业。（有关系，有资金，为什么不干呢）<br/>考不上公务员，进不了国企的，走投无路的平民子女，也只能硬着头皮去创业。<br/>不要指望政府扶持。先想办法去挣富二代创业者的钱。<br/>他们人傻钱多好沟通。不信你试试。</p>', '2016-12-25 08:35:01', 0),
(42, 95, 91, '<p>大学期间创业的，主要有4种人。<br/>第一种：技能型。也是最容易成功的类型。这种人通常专精与某种技术，比如设计、编程等等。因为感兴趣，从小就开始积累。以至于刚进大学的时候就是专业学霸，没事做个小网站，小外挂什么的。到了大二自然是各种外快实践，给大企业做软件画册，积累了足够的资源经验以后拉两个同学出来单干。毫无疑问，这种是成功几率最高的。我所在的学校出过几个这样的人才，上过央视致富经什么的，都是此类。<br/>第二种：实践型。成功的概率仅次于第一种。特点是少说话多干事。虽然技能不是很强，但是人际关系处理的不错，朋友不多但是质量很高。有毅力，有领导力。即使卖茶叶蛋也能持之以恒，从不高调炫耀或者自怨自艾，即使卖袜子也十分认真。这种人往往在传统行业做得比较出色，开个餐馆水果店什么的，也可以很成功。<br/>第三种：富二代。这类人的特点是有钱，有想法，没技能。经常在朋友圈分享一些高大上的资讯然后很幽怨的跟你说，我手里有xx万，想干点什么，就是不知道往哪投。他们真正去创业的时候往往开着奥迪A6，会买最好的设备，清一色的iMAC，整齐的制服，最后往往什么也做不出来。想成功的话，基本都要拉拢前两种人<br/>第四种：励志型。人数最多的一种，基本是给其他人垫背的。一没技能二没毅力，满嘴的马云李开复，张口闭口大数据，脑子里全是心灵鸡汤。好像谁的成功都可以复制，每天都走在成功路上。而且这种人特别容易抱团，各种励志讲座各种训练营是他们最爱去的地方。有的最后只剩一句话：你听说过安利吗？</p><p><br/></p>', '2016-12-25 08:41:48', 0),
(41, 95, 12, '<p>说大话容易。<br/>我很不同意为了解决大学生就业难的问题，就鼓励他们去创业。那干嘛不鼓励有经验的人创业，让大学生去这些创业企业就业呢？<br/>大批大学生找不到工作的社会问题大？<br/>还是大批大学生花光父母积蓄创业又失败，仍找不到工作的社会问题大？<br/>让连就业都困难的大学生去创业，这不是缺心眼吗？</p>', '2016-12-25 08:36:34', 0),
(43, 95, 82, '<p>政府没弹药了，所以支持创业了，如果是因为找不到工作就创业，那要考虑清楚，到底是自己能力欠缺，还是自己适合创业</p>', '2016-12-25 08:42:24', 0),
(44, 85, 82, '<p>要参加。就算你对算法和解题不感兴趣，以后手机APP可能也用不上特别高端的算法，能参加ACM至少是个积累代码量的好机会。我个人的感觉，写了2K只是熟悉语法，写了5K才算掌握语言，写到15K你对实际的工程开发有个概念，写过40K才敢说设计和架构，等你代码量突破100K的时候，你已经可以进入顶尖程序员的行列了。加油吧。<br/><br/></p><p><br/></p>', '2016-12-25 08:42:57', 0),
(45, 85, 12, '<p>你对算法和解题不感兴趣而且以后手机APP可能也用不上特别高端的算法</p>', '2016-12-25 08:43:52', 0),
(46, 85, 96, '<p>有能力还是参加好<img src="http://img.baidu.com/hi/jx2/j_0012.gif"/><img src="http://img.baidu.com/hi/jx2/j_0023.gif"/></p>', '2016-12-25 08:44:26', 0),
(47, 85, 91, '<p>软件开发往高处走靠得是数学。<img src="http://img.baidu.com/hi/jx2/j_0003.gif"/><img src="http://img.baidu.com/hi/jx2/j_0004.gif"/><img src="http://img.baidu.com/hi/jx2/j_0057.gif"/></p>', '2016-12-25 08:45:16', 0),
(48, 92, 91, '<p>做下面这些事中的绝大多数，答主初衷就是想认识有趣的人经历有趣的事，最后的收获也没有违背，和玩知乎的初衷一样，有人评论题主此问为无意义的征集类问题，我想说非也。如果题主现实生活中接触到的同学朋友圈子很有限，如果没有这个问题和这下面的众多答案，题主又如何去知道“原来有这么多和我同龄的人已经在原本同样空白的时间卷轴上画出了如此之多的斑斓色彩“，所以写这个答案除了所谓的分享和秀，更多的是想鼓励题主在有限的时间里，尽你最大的力量去做你想做的事。<br/>休息空档写一张，事情不分先后，图片均为实拍皂片，借用的私信说一下哟～<br/>1.旅行。小长假一个人去远一点的地儿慢慢逛游，周末只能苏锡常沪宁溜达。大一一年各种交通工具票+门票存起来一大盒翻出来看时炒鸡有意思！就不秀了，谁存谁知道。其实苏州园林辣么多辣么美其实够看，答主属于一个小怡园都能分四个季节去逛的园林脑残粉ˊ_&gt;ˋ</p><p><br/></p><p><br/></p>', '2016-12-25 08:46:07', 0),
(49, 92, 12, '<p>工科生，大四待毕业。<br/>大一大二平时上课作业自习。周末社团可以学习到其他内容，最后靠学到的在大三大四小挣了一些。<br/>大三大四平时上课作业自习参加比赛。最后比赛拿了两个国奖也比较开心。<br/>期间节假日和同学出去玩，大学四年也算在国内转了不少地方。<br/>可能没有别人那么的精彩，但是回头看看四年还是比较满足。</p><p><br/></p><p><br/></p>', '2016-12-25 08:47:56', 0),
(50, 92, 82, '<p>做数学作业 做编程作业 做各种作业 刷题应付期末考试 我就不信把所有作业都完成还有课余时间</p>', '2016-12-25 08:48:24', 0),
(51, 92, 96, '<p>大一课余时间基本都在训练，在校队打乒乓球，打比赛。<br/>大二开始带家教，因为钱不够花啊。一周有三天要去，去的话一晚上基本没了，因为离得太远。不去家教的时候帮老师带师弟训练，虽然大二我基本退队不打了。<br/>大三专业课很多，课余时间比较少，会做一些小玩意，都是一些设计类的，顺便搜集考研资料，准备跨专业考研。<br/>大四复习半年，后半年复试，复试结束了和导师联系，看书。<br/>研一选了20多门课，跨专业伤不起啊。<br/>研二偶的机会出去实习，去了某研究所，每天8点到21点，全年单休，清明端午不放假。参加单位的比赛拿了乒乓球冠军，健美操第三名，跳绳N个冠军，长跑一个冠军。<br/>研三回来答辩，顺便再转专业读博士。<br/>现在博士ing.一边发paper一边健身。</p><p><br/></p>', '2016-12-25 08:49:03', 0);

