--
-- 表的结构 `news`
--

CREATE TABLE IF NOT EXISTS `news` (
  `newsid` int(11) NOT NULL AUTO_INCREMENT COMMENT '新闻id',
  `newstitle` char(255) NOT NULL COMMENT '新闻标题',
  `newssource` char(255) NOT NULL COMMENT '新闻来源',
  `newstime` date NOT NULL COMMENT '新闻时间',
  `newscontent` text NOT NULL COMMENT '新闻内容',
  `newsthumb` varchar(255) NOT NULL COMMENT '新闻图片缩略图',
  PRIMARY KEY (`newsid`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COMMENT='热点资讯新闻表' AUTO_INCREMENT=19 ;

--
-- 转存表中的数据 `news`
--

INSERT INTO `news` (`newsid`, `newstitle`, `newssource`, `newstime`, `newscontent`, `newsthumb`) VALUES
(7, '2016年度青年教师教学技能竞赛', '河北师范大学', '2016-12-06', '<p style="text-indent:2em;font-size:14px;font-family:''Microsoft YaHei'';background-color:#FFFFFF;">\r\n	由学校教务处、人事处、工会联合组织的2016年度河北师范大学青年教师教学技能竞赛决赛于12月9日圆满落幕。本次竞赛在教师个人申报的基础上，分学院选拔和学校决赛两个阶段进行，其中决赛内容涵盖教学设计、课堂教学、教学反思等三个部分，对教师教学能力进行了全方位综合考察。\r\n</p>\r\n<p style="text-indent:2em;font-size:14px;font-family:''Microsoft YaHei'';background-color:#FFFFFF;">\r\n	最终进入学校决赛的44名教师按照专业类别分为理科组、文科组和艺术技能组分别于12月7日-9日展示了各自的课堂教学特色和教学风采。参赛教师精彩的讲授和灵活教学方法的运用赢得了在场评委、教师和学生的热烈掌声。经过紧张而激烈地角逐，来自生命科学学院的李秋石、商学院的刘彦超、体育学院的赵丽娜等三位教师分别获得理科组、文科组和艺术技能组一等奖，教育学院的潘新民等6位教师获得二等奖，物理科学与信息工程学院的马艳华等 15位教师获得三等奖。\r\n</p>\r\n<p style="text-indent:2em;font-size:14px;font-family:''Microsoft YaHei'';background-color:#FFFFFF;">\r\n	为确保本次比赛顺利进行，学校成立了以副校长邓明立为组长的领导小组，同时由教务处、人事处、工会等部门工作人员组成工作组，负责竞赛的具体组织实施。各学院也分别成立了以院长为组长的竞赛工作组，组织本学院的初赛。整个赛事前后历时近2个月，受到了全校师生的广泛关注。\r\n</p>\r\n<p style="text-indent:2em;font-size:14px;font-family:''Microsoft YaHei'';background-color:#FFFFFF;">\r\n	我校青年教师教学技能竞赛从2002年举办以来，比赛形式、评价方式不断丰富发展，为进一步提升广大青年教师的教学能力和业务水平，促进教师间的业务交流起到了重要的推动作用。\r\n</p>', 'Uploads/Uploads2016-12-19/ctdxy1.jpg'),
(8, '邓明立为教务处党支部上专题党课', '河北师范大学', '2016-12-14', '<p style="text-indent:2em;font-size:14px;font-family:''Microsoft YaHei'';background-color:#FFFFFF;">\r\n	12月15日下午，副校长邓明立在行政楼201会议室为教务处党支部全体党员上专题党课，结合学校教学工作对习近平总书记在全国高校思想政治工作会议上的重要讲话进行了分析解读。\r\n</p>\r\n<p style="text-indent:2em;font-size:14px;font-family:''Microsoft YaHei'';background-color:#FFFFFF;">\r\n	邓明立指出，关于加强高校思想政治工作，习近平在讲话中强调了“一个根本问题、两个一流、三条规律、四个服务、五个坚持、六个认识、七个“多””，每一个主题都与我们的工作息息相关。习近平总书记的重要讲话精辟阐述了加强和改进高校思想政治工作的重大意义、根本方向、目标任务和基本要求，系统回答了事关高等教育事业发展和高校思想政治工作的一系列重大问题，为做好新形势下高校思想政治工作指明了方向。我们要在领会习总书记讲话精神的基础上，结合工作实际，认真贯彻落实。高校思想政治工作关系到高校培养什么样的人、如何培养人以及为谁培养人这个根本问题，教务处要从加强思想政治理论课建设入手，严把教材选用关，鼓励思想政治理论课教师创新教学方法，讲好课，培养和弘扬社会主义核心价值观。结合邓明立对习总书记讲话精神的解读，教务处党员展开了热烈的讨论，表示要齐心协力，以讲话精神为指导，从创新做好本职工作入手，为学校人才培养贡献力量。\r\n</p>\r\n<p style="text-indent:2em;font-size:14px;font-family:''Microsoft YaHei'';background-color:#FFFFFF;">\r\n	最后，邓明立勉励全体党员要有事业心、责任心、进取心、平常心、仁爱之心，发挥党员的模范带头作用，以实际行动积极推进学校“十三五”规划的落实和“双一流”大学建设的步伐。\r\n</p>', 'Uploads/Uploads2016-12-22/xzlbcxl.jpg'),
(9, '我校举办统战理论专题培训班', '河北师范大学', '2016-12-21', '<p style="text-indent:2em;font-size:14px;font-family:''Microsoft YaHei'';background-color:#FFFFFF;">\r\n	12月14日上午，我校在河北省社会主义学院举办统战理论专题培训班。各二级基层党组织书记及党校、组织部、宣传部、统战部负责人等39人参加了培训。\r\n</p>\r\n<p style="text-indent:2em;font-size:14px;font-family:''Microsoft YaHei'';background-color:#FFFFFF;">\r\n	培训班特邀河北省社会主义学院教学部副主任王树臣教授和河北省社会主义学院党组副书记、副院长姜虹教授分别以《十八大以来多党合作的几个问题》和《谈统战工作的艺术》为题举办两场讲座。王树臣和姜虹详细介绍了多党合作制度的形成、发展和特点，对高校各级党委如何做好新时期的统战工作、安排党外代表人士的基本政策等进行了丰富生动的讲解。\r\n</p>\r\n<p style="text-indent:2em;font-size:14px;font-family:''Microsoft YaHei'';background-color:#FFFFFF;">\r\n	培训班由党校常务副校长白振祥主持。白振祥指出，为认真贯彻党的统一战线方针政策，进一步落实中央统战工作会议、《中国共产党统一战线工作条例（试行）》、全国高校统战工作会议精神，河北省高校统战工作会议要求，校党委把统战理论培训作为对党员领导干部的重要培训内容之一，组织开展了本次培训，各基层党委书记一定要深刻认识其重要意义，认真学习；通过培训，强化党委对统战工作的主体责任，推进高校统一战线工作创新，建立长效机制，形成工作合力，着力构建党委统一领导、有关方面各负其责的“大统战”工作格局，确保中央和省委部署要求落到实处。\r\n</p>\r\n<p style="text-indent:2em;font-size:14px;font-family:''Microsoft YaHei'';background-color:#FFFFFF;">\r\n	通过开展此次统战理论培训，切实增强了做好新形势下高校统战工作的责任感和使命感，为今后做好统战工作打下了坚实的政策理论基础。\r\n</p>', 'Uploads/Uploads2016-12-22/ctdxy2.jpg'),
(10, '河北省动物学会第八届理事会年会', '河北师范大学', '2016-12-21', '<p style="text-indent:2em;font-size:14px;font-family:''Microsoft YaHei'';background-color:#FFFFFF;">\r\n	12月16-17日，由河北省动物学会主办、生命科学学院承办的河北省动物学会第八届理事会2016年会在我校隆重召开。来自河北大学、河北师范大学、河北医科大学、河北农业大学等所12所高等院校、河北省林业厅、河北科学技术出版社、石家庄市动物园和重点中学的26位理事、专家参加了会议。\r\n</p>\r\n<p style="text-indent:2em;font-size:14px;font-family:''Microsoft YaHei'';background-color:#FFFFFF;">\r\n	会议由河北省动物学会秘书长、河北大学谢松教授主持，河北师范大学副校长刘敬泽教授代表学校致欢迎词，并作为学会副理事长向全会传达了“中国动物学会第十七届理事会第三次会议”精神。学会理事长河北大学任国栋教授作了题为“河北省动物学会2016年工作总结暨2017年工作计划”的大会报告。\r\n</p>\r\n<p style="text-indent:2em;font-size:14px;font-family:''Microsoft YaHei'';background-color:#FFFFFF;">\r\n	与会的各位理事和专家从学术交流、科普教育、科学研究、科技服务等几个方面对学会的各项工作予以充分肯定，针对学会下一步发展计划和存在的问题展开热烈讨论，提出了大量建设性意见和建议。与会代表一致表示，要利用学会专业特长和人才优势主动服务社会；努力营造科技创新氛围，发挥学术活动推动科技创新的促进作用；创新学会管理，打造品牌学会。\r\n</p>', 'Uploads/Uploads2016-12-22/xx.jpg'),
(15, '我校承办河北省2016年中高职衔接试点院校专业带头人和专业课教师培训', '河北师范大学', '2016-12-22', '<p style="text-indent:2em;font-size:14px;font-family:''Microsoft YaHei'';background-color:#FFFFFF;">\r\n	12月20日上午，由我校河北省职业技术教育研究所承办的河北省2016年中高职衔接试点院校专业带头人和专业课教师培训班在职业技术学院开班。河北省教育厅副厅长贾海明、副校长刘敬泽以及来自全省中高职衔接试点院校和设区市教育局的代表近100人出席了开班仪式。河北省职业技术教育研究所所长、职业技术学院院长陈嘉兴教授主持开班典礼。\r\n</p>\r\n<p style="text-indent:2em;font-size:14px;font-family:''Microsoft YaHei'';background-color:#FFFFFF;">\r\n	刘敬泽在致辞中指出，基础教育、职业教育和高等教育师资培养“三教并举”，是河北师范大学坚定不移的办学思路，目前已构建了培养培训一体化的教师教育体系。在职业教育领域形成了职业技术学院、中燃工学院、河北省职业技术教育研究所三位一体的办学格局。2014年，河北师范大学职业技术学院被省教育厅遴选为首批“中职与普通本科‘3+4’分段培养”试点高校之一，明年将迎来第一批“3+4”分段培养的学生。我们将充分发挥自身优势，以河北省“3+4”分段培养试点为契机，深化校企融合人才培养模式改革，推进专业与产业、课程内容与职业标准、教学过程与生产过程对接，创新应用型人才培养模式，为我省现代职业教育体系建设做出更大贡献。\r\n</p>\r\n<p style="text-indent:2em;font-size:14px;font-family:''Microsoft YaHei'';background-color:#FFFFFF;">\r\n	贾海明副厅长在讲话中指出，我省中高职衔接从1999年试点工作实施以来，在打通和拓宽职业院校毕业生的上升通道，增强职业院校的吸引力，促进职业院校教育教学改革，促进本科院校转型发展等方面取得了一定的成效，但同时也要认识到我省中高职衔接试点工作依然存在培养目标定位模糊、专业设置不够契合、课程内容重复率高、办学不规范等问题。他特别指出，很多事物的成长都有先发展后规范的过程，中高职衔接现在已经到了需要规范的时候了。希望引起大家高度重视，立即着手规范中高职衔接试点工作，尤其要在人才培养过程的各个环节衔接上下功夫,保质量，促发展。\r\n</p>\r\n<p style="text-indent:2em;font-size:14px;font-family:''Microsoft YaHei'';background-color:#FFFFFF;">\r\n	本次培训班是河北省教育厅职业教育师资专项培训，为期10天，旨在为广大试点院校提供学习交流平台，促进中等学校与高等院校充分对接，推进试点工作深入开展。\r\n</p>\r\n<p style="text-indent:2em;font-size:14px;font-family:''Microsoft YaHei'';background-color:#FFFFFF;">\r\n	中职与本科3+4分段培养模式是我省2014年开始试点的一种中高职衔接人才培养模式，是我省推进现代职业教育体系建设的重要组成部分。“3+4”分段培养模式的前3年列入中等职业学校招生计划，后4年列入有关本科高校招生计划；前3年期满后，对有继续学习意愿的学生经考核测试合格后可进入后4年本科高校学习。其他学生颁发中等职业学校毕业证书，按中等职业学校毕业生对待。\r\n</p>', 'Uploads/Uploads2016-12-22/585b458e9a7ed.jpg'),
(16, '学校纪委举行2016年度履职廉政谈话', '河北师范大学', '2016-12-22', '<p style="text-indent:2em;font-size:14px;font-family:''Microsoft YaHei'';background-color:#FFFFFF;">\r\n	12月20日下午，学校纪委书记李榕专门对各基层党组织和机关部门主要负责人进行了履职廉政谈话。李榕从推进党风廉政建设和反腐败工作的高度，结合党要管党、从严治党的要求，对落实党风廉政建设主体责任重要性进行了深入阐述，要求各基层党组织切实履行好主体责任，充分运用好“四种形态”。\r\n</p>\r\n<p style="text-indent:2em;font-size:14px;font-family:''Microsoft YaHei'';background-color:#FFFFFF;">\r\n	结合省委巡视组反馈意见和校纪委受理问题线索信访件的情况，在分析了我校党风廉政工作中存在的问题后，李榕就认真落实党风廉政建设责任，对大家提出五个方面的要求：一要坚定理想信念，增强政治定力。带头真学真懂真信真用党的科学理论，多吸理论之氧，常补精神之钙。二要提高认识，增强责任自觉。各单位主要负责人是党风廉政建设第一责任人，要主动履责、积极负责、敢于担责，发挥好带头示范作用。三要挺纪在前，严守政治纪律和政治规矩。树立纪律观念和规矩意识，按原则按制度按程序履职尽责。四要加强作风建设，带头执行中央八项规定精神。在贯彻执行中央八项规定精神上不懈怠、不敷衍、不放松，关注小事小情小节，特别要对隐形、变相的“四风”问题保持高度警觉，不搞变通，不打擦边球。五要严于律己，率先垂范。做履职尽责的表率，做勇于担当的表率，做廉洁自律的表率。\r\n</p>\r\n<p style="text-indent:2em;font-size:14px;font-family:''Microsoft YaHei'';background-color:#FFFFFF;">\r\n	李榕强调，当前要深入学习贯彻党的十八届六中全会精神，把学习贯彻全会精神与遵守党章结合起来，与学习贯彻习近平总书记系列重要讲话精神结合起来，与学习贯彻全国高校思想政治工作会议和省第九次党代会精神结合起来，扎实推进学校“双一流”建设和“十三五”规划各项工作落实，为学校改革发展做出不懈努力。\r\n</p>', 'Uploads/Uploads2016-12-22/585b45b909176.jpg'),
(17, '后勤服务集团举办“走进饮食一日体验”活动', '河北师范大学', '2016-12-23', '<p style="text-indent:2em;font-size:14px;font-family:''Microsoft YaHei'';background-color:#FFFFFF;">\r\n	为进一步了解学生就餐需求，增进相互理解，创建和谐的饮食环境，12月18日，后勤服务集团饮食服务中心在校团委的大力支持下联合学生会举办了“走进饮食一日体验”活动。活动由劳动体验、参观餐厅和座谈交流三部分组成。\r\n</p>\r\n<p style="text-indent:2em;font-size:14px;font-family:''Microsoft YaHei'';background-color:#FFFFFF;">\r\n	凌晨5点半，校学生会权益部代表及各学院学生代表65人来到饮食中心，同学们被分配到加工部、第一、二、四、五餐厅、清真餐厅、宴会小餐厅的不同岗位，同师傅们开始准备一天的饭菜。他们有的择菜，有的分菜，有的包馄饨，有的揉馒头……。开餐时，同学们来到了售饭岗位，当起了“服务员”。期间，同学们参观了饮食中心，并进行了优秀窗口的评选。\r\n</p>\r\n<p style="text-indent:2em;font-size:14px;font-family:''Microsoft YaHei'';background-color:#FFFFFF;">\r\n	下午3点，在饮食中心会议室召开了座谈会。会上，后勤服务集团副总经理张艳国对原材料采购、农药残留检测、初加工流程、绝不售卖剩饭剩菜等情况做了介绍。学生代表从食品质量、卫生、服务态度、饭菜价格、就餐秩序、设施等方面提出了意见和建议。饮食服务中心对学生提出的问题进行了一一解答，表示在今后的工作中改进不足，通过培训、教育等方式，不断提高员工素质和技能水平，尽最大努力满足同学们的就餐需求。\r\n</p>\r\n<p style="text-indent:2em;font-size:14px;font-family:''Microsoft YaHei'';background-color:#FFFFFF;">\r\n	交流过后，校团委书记马建国讲话。他希望与会的同学们通过各种途径宣讲饮食中心在食品安全方面所做的工作。并希望这种交流活动成为一种常态化机制，形成一个相互沟通的桥梁和纽带。张艳国副总经理指出，饮食工作需要不断的改革，以更加贴近于同学的需求。饮食服务没有最好，只有更好。要建立多种沟通渠道，做到及时发现问题，及时解决问题，永远做广大同学利益的维护者、保护者。校团委、后勤服务集团有关领导、学生代表及饮食服务中心班子成员、部门经理参加了座谈。\r\n</p>', 'Uploads/Uploads2016-12-22/585b45dc0185e.jpg'),
(18, '中国共产党革命精神与文化资源研究中心举办赶考精神研究学术研讨会', '河北师范大学', '2016-12-23', '<p style="text-indent:2em;font-size:14px;font-family:''Microsoft YaHei'';background-color:#FFFFFF;">\r\n	12月17日，在纪念毛泽东诞辰123周年之际，为深入学习贯彻党的十八届六中全会精神和习近平总书记关于全面从严治党的重要讲话精神，教育部人文社会科学重点研究基地重大项目“‘赶考’精神与提高党的执政能力研究”课题组、我校中国共产党革命精神与文化资源研究中心主办了 “‘赶考’精神与提高党的执政能力研究暨纪念毛泽东诞辰123周年”学术研讨会。来自河北省委党史研究室、河北省社科院、河北省委党校、河北经贸大学、河北医科大学、河北省社会主义学院、西柏坡纪念馆、石家庄市社科院、石家庄市委党校、石家庄日报社及我校中共革命精神与文化资源研究中心的40余位专家学者参加研讨活动。教育部社科中心党建思政研究处副处长朱喜坤莅会指导，我校党委副书记、副校长、中共革命精神与文化资源研究中心主任戴建兵教授在开幕式致辞，研究中心常务副主任姚志军主持研讨会。课题组负责人张志永教授代表课题组成员做会议总结并致谢。\r\n</p>\r\n<p style="text-indent:2em;font-size:14px;font-family:''Microsoft YaHei'';background-color:#FFFFFF;">\r\n	在研讨交流阶段，与会专家学者从不同学科知识背景出发，踊跃发言，场面热烈。研讨内容紧密围绕会议主题，涵盖了赶考精神的形成及内容、赶考精神的时代价值、党的执政能力的内涵、提高党的科学执政民主执政和依法执政能力、推进国家治理体系和治理能力现代化建设以及“七一”讲话精神、党的十八届六中全会精神与党的执政能力建设等多方面内容。研讨内容丰富，涵盖面广，对课题组顺利推进下一阶段的研究工作，瞄准学术前沿和时代特征推出高水平的研究成果，具有重要的启示和借鉴意义。\r\n</p>\r\n<p style="text-indent:2em;font-size:14px;font-family:''Microsoft YaHei'';background-color:#FFFFFF;">\r\n	研讨环节结束后，姚志军就研究中心成立以来，尤其是2016年度开展的各项重点工作进行简要介绍，通报了首次开展的年度自设项目课题立项工作，希望与会专家学者一如既往关心支持研究中心建设发展。会议为与会专家学者和获得2016年度中心课题立项的研究人员颁发兼职研究员聘书。当日下午，部分与会专家和课题组成员继续开会，就修改课题提纲及书稿等事宜进行了深入研讨，消化吸收会议研讨成果，完善课题工作方案。\r\n</p>\r\n<p style="text-indent:2em;font-size:14px;font-family:''Microsoft YaHei'';background-color:#FFFFFF;">\r\n	本次研讨会是研究中心以重大项目课题组为建设单元，围绕基地“学术高地”“红色智库”“宣教阵地”建设目标，开展综合性、一体化建设发展的初次尝试。研讨会不仅在学术上达到了充分研究“赶考”精神的丰富内涵，探讨其在新时期的时代价值，为中国共产党有效应对“四大考验”、克服“四大危险”，不断提升党的执政能力提供精神支撑和动力源泉的目的，在研究中心管理体制调整创新上也取得积极成果。\r\n</p>', 'Uploads/Uploads2016-12-22/585b45fec5525.jpg');

